import uvicornfrom src.utils import setup_loggingfrom src.config import PORTfrom src.interface.backdoor import BackdoorInterfacefrom src.interface.web import WebInterfacefrom src.interface.chat import ChatInterfacedef main():    setup_logging()    app = None    for interface in [        ChatInterface(),        BackdoorInterface(),        WebInterface(),    ]:        app = interface.setup_app(app)    uvicorn.run(app, host="0.0.0.0", port=PORT)if __name__ == '__main__':    main()