import warningsfrom langchain.chains.base import Chainfrom pydantic import Fieldfrom typing import List, Anyfrom abc import ABC, abstractmethodfrom src.base.chain import LLMChainModuleclass LLMModule(Chain):    """    Responsible for injecting a large language model (LLM) instance into an LLMChainModule,    creating an inference module that combines the prompt with the large model.    """    chain: LLMChainModule = Field(...)    @classmethod    def from_llm(cls, llm, prompt, verbose: bool = False, **kwargs) -> "LLMModule":        """        This method is responsible for connecting a built large language model with an LLMChainModule,        creating an executable large language model inference module.        Args:            llm: The large language model instance.            prompt: The original prompt (containing abstract placeholders).            verbose: Execution mode.            **kwargs: All other parameters.        Returns:            LLMModule: An executable large language model inference module.        """        chain = LLMChainModule.from_llm(llm=llm, prompt=prompt, verbose=verbose)        return cls(chain=chain, verbose=verbose, **kwargs)    @property    def input_keys(self) -> List[str]:        """        Retrieves the names of the placeholders in the current large model inference module's prompt.        Returns:            List[str]: The names of the placeholders in the prompt.        """        return self.chain.input_variables    @property    def output_keys(self) -> List[str]:        """        Retrieves the keywords output by the current large model inference module.        Returns:            List[str]: The output keywords.        """        return []    @property    def _chain_type(self) -> str:        """        Retrieves the type of the current inference module.        Returns:            str: The type of the inference module.        """        return getattr(self.chain,'chain_type','unknown')    def _call(self, *args, **kwargs) -> Any:        """        This method is deprecated and is now defined by abstract subclasses to execute inference.        Args:            *args: Arguments.            **kwargs: Keyword arguments.        Returns:            Any: The inference result.        Note:            This method is deprecated. Please refer to subclass implementations for executing inference.        """        warnings.warn("This method is deprecated. Please refer to subclass implementations.")