import randomimport warningsfrom typing import Anyfrom src.config import AZURE_OPENAI_API_KEY, AZURE_OPENAI_API_BASEfrom src.config import GPT_MODELfrom langchain.chat_models import ChatOpenAIclass MyChatOpenAI(ChatOpenAI):    """    This class is responsible for establishing a communication pathway with llm models,    including verifying authentication information and selecting the appropriate llm model.    """    def __init__(self, **kwargs: Any) -> None:        """        Initializes the class with core parameters required for calling the llm model,        including the API key, the model identifier, and the temperature value setting for the model invocation.        Args:            **kwargs: Necessary parameters for the operation.        The method performs several checks on the provided keyword arguments (`kwargs`):        - If 'gpt_model' is not provided, a warning is issued, and a default model identifier is set.        - If 'temperature' is not provided, a warning is issued, and a default temperature value is set.        - If 'open_ai_key' is missing, default values for the OpenAI API key and base URL are chosen from predefined        lists.        Finally, the superclass constructor is called with the potentially modified `kwargs`, and these are saved for        future use.        """        # Check if 'gpt_model' is present in kwargs, issue a warning if not, and set a default value        if 'model_name' not in kwargs:            warnings.warn(f"'gpt_model' not provided in kwargs. Please specify a GPT model. Now set the default value "                          f"{GPT_MODEL}")            kwargs['model_name'] = GPT_MODEL        # Check if 'temperature' is present in kwargs, issue a warning if not, and set a default value        if 'temperature' not in kwargs:            warnings.warn("'temperature' not provided in kwargs. Please specify a temperature value. Now set the "                          "default value 0")            kwargs['temperature'] = 0        # If 'open_ai_key' is not in kwargs, use default values for the OpenAI API key and base URL        if 'openai_api_key' not in kwargs or kwargs.get('openai_api_ky') is None:            kwargs['openai_api_key'] = AZURE_OPENAI_API_KEY        kwargs['openai_api_base'] = AZURE_OPENAI_API_BASE        # Call the superclass constructor with possibly modified kwargs        super().__init__(**kwargs)        # Save the kwargs for potential future use        # self._lc_kwargs = kwargs