import jsonimport timeimport requestsfrom src.base.private import PrivateModelclass BaiChuan2M7B(PrivateModel):    def inference(self, prompt: str) -> str:        try:            # 计算提示文本的长度            current_length = len(prompt)            # 构造请求的数据，包括输入文本和参数            data = {                "model": "llm",                "messages": [{"role": "user", "content": prompt}],                "temperature": 0.1,                "stream": False            }            # 记录请求开始的时间            start_time = time.time()            # 发送POST请求            url = "http://inference-internal.yanxi-cap.jd.com/inference/service-81kcvavl1x/v1/chat/completions"            response = requests.post(url, data=json.dumps(data), stream=True)            # 检查HTTP响应状态码            if response.status_code == 200:                result = ""                # ***********************************************************************                # 读取响应内容的每个块，并进行处理                ret = response.json()                # print(ret)                result += ret.get("choices", {})[0].get("message", {}).get("content", "")                # ***********************************************************************                # 返回累加的结果                return result            else:                # 如果响应状态码不是200，打印错误信息                # print(f"请求失败，状态码：{response.status_code}, {response.text}")                return ""        except requests.exceptions.RequestException as e:            # 打印请求异常            # print(f"请求异常：{e}")            return ""